var item;
var res_robosem;
var res_motion_checker;
var is_empty;
var res;
var robosem_res;
var motion_checker_res;
var isworking_robosem;
var result;
var message;
var hue_id;
var hue_color;

function welcome(res_robosem, res_motion_checker) {
  setTimeout(function(){ Fiber(function(){  $engine.scheduledPublish(res_robosem, "play_tts", "std_msgs/String", ({"data":'안녕하세요. 먼저 회의실을 체크할께요'}));$engine.scheduledPublish(res_robosem, "robot_motion", "std_msgs/String", ({"data":'HAPPY3'}));UDF_set_light([3, 1, 2], 'WHITE', null, null, 'WHITE');check_room_ex(res_motion_checker);
    $engine.sleep(2000);$engine.scheduledPublish(res_robosem, "play_tts", "std_msgs/String", ({"data":'빨간불이 켜진 방이 현재 사용중이고, '}));$engine.scheduledPublish(res_robosem, "robot_motion", "std_msgs/String", ({"data":'LEFTATTENTION'}));$engine.sleep(4000);$engine.scheduledPublish(res_robosem, "play_tts", "std_msgs/String", ({"data":'녹색불이 켜진 방이 비어있는 회의실 입니다.'}));$engine.scheduledPublish(res_robosem, "robot_motion", "std_msgs/String", ({"data":'RIGHTATTENTION'}));$engine.sleep(5500);$engine.print("Rocon Authoring :" + JSON.stringify(is_empty));if (is_empty == true) {
      $engine.scheduledPublish(res_robosem, "play_tts", "std_msgs/String", ({"data":'그럼 문을 열어 놓을께요. 안녕히가세요'}));$engine.scheduledPublish(res_robosem, "robot_motion", "std_msgs/String", ({"data":'BYELEFTHAND'}));$engine.pub('/door_ctrl', 'std_msgs/Bool', ({data:true}));} else {
      $engine.scheduledPublish(res_robosem, "play_tts", "std_msgs/String", ({"data":'비어있는 회의실이 없습니다. 나중에 다시 서비스를 시작해 주세요.'}));$engine.scheduledPublish(res_robosem, "robot_motion", "std_msgs/String", ({"data":'BYELEFTHAND'}));UDF_set_light([1, 2, 3], 'OFF', null, null, 'OFF');}
    $engine.print("Rocon Authoring :" + JSON.stringify('[Welcome] Welcome End'));$engine.sleep(3000);UDF_set_light([3], 'OFF', null, null, 'OFF');$engine.sleep(8000);UDF_set_light([1, 2], 'OFF', null, null, 'OFF');isworking_robosem = false;
  }).run(); }, 0);}

function check_room_ex(res) {
  $engine.runScheduledAction(res, "check_motion", "rocon_ninja_block/CheckMotionAction", ({"device_name":''}), function(item){   $engine.print("Rocon Authoring :" + JSON.stringify('[Welcome] Result'));is_empty = false;
    if (item['success'] == true) {
      for (var result_index in item['is_detected']) {
        result = item['is_detected'][result_index];
        message = 'empty';
        hue_id = 1;
        hue_color = 'RED';
        if (result['key'] == 'motion_detector') {
          hue_id = 1;
        } else if (result['key'] == 'motion_detector_2') {
          hue_id = 2;
        } else {
          $engine.print("Rocon Authoring :" + JSON.stringify('Unknown key'));$engine.print("Rocon Authoring :" + JSON.stringify(result['key']));}
        if (result['value'] == 'True') {
          message = 'occupied';
          hue_color = 'RED';
        } else if (result['value'] == 'False') {
          is_empty = true;
          message = 'empty';
          hue_color = 'GREEN';
        } else {
          $engine.print("Rocon Authoring :" + JSON.stringify('Unknown value'));$engine.print("Rocon Authoring :" + JSON.stringify(result['value']));}
        $engine.print("Rocon Authoring :" + JSON.stringify([result['key'],': ',message].join('')));UDF_set_light([hue_id], hue_color, null, null, hue_color);}
    }
  }, function(item){ });}


$engine.print("Rocon Authoring :" + JSON.stringify('[Welcome] Start Welcome workflow'));var robosem_res = ($engine.allocateResource("robosem_bridge/robosem_bridge", "rocon:/pc/robosem_bridge#robosem_bridge/robosem_bridge", [{"remap_from":"play_tts","remap_to":"/play_tts"},{"remap_from":"robot_motion","remap_to":"/robot_motion"},{"remap_from":"touch_sensor_event","remap_to":"/touch_sensor_event"}], [], {"timeout":25000000,"type":"static"})); $engine.print("Rocon Authoring :" + JSON.stringify('Allocation robosem resource'));var motion_checker_res = ($engine.allocateResource("rocon_ninja_block/motion_checker", "rocon:/pc/motion_checker#rocon_ninja_block/motion_checker", [{"remap_from":"ninja_block_data","remap_to":"/ninja_block_data"},{"remap_from":"check_motion","remap_to":"/check_motion"}], [], {"timeout":25000000,"type":"static"})); $engine.print("Rocon Authoring :" + JSON.stringify('Allocation robosem motion checker'));isworking_robosem = false;

$engine.scheduledSubscribe(robosem_res, 'touch_sensor_event', 'std_msgs/String', function(item){   if (item['data'] == 'right') {
    if (isworking_robosem == false) {
      isworking_robosem = true;
      welcome(robosem_res, motion_checker_res);
    }
  } else if (item['data'] == 'left') {
    if (isworking_robosem == false) {
      isworking_robosem = true;
      welcome(robosem_res, motion_checker_res);
    }
  } else {
  }
 });
$engine.subscribe('/beacons', 'std_msgs/String', function(item){   $engine.print("Rocon Authoring :" + JSON.stringify(String('[Welcome] Subscribe Beacons: ') + String(item['data'])));if (item['data'] == 'D0:39:72:A3:DE:75') {
    if (isworking_robosem == false) {
      isworking_robosem = true;
      welcome(robosem_res, motion_checker_res);
    }
  }
 });
is_empty = false;
